mapper.delete:
delete from dish
where businessman_id = #{businessmanId}
and id in

    #{id}

mapper.update:
update dish set
name = #{dish.name},price=#{dish.price},is_preferential=#{dish.isPreferential},weight=#{dish.weight}
,update_time=#{dish.updateTime}
where businessman_id=#{currentId} and id=#{dish.id}
